import React, { useState } from "react";
import Logo from './Logo.png'
import "bootstrap/dist/css/bootstrap.min.css";
import { useNavigate } from 'react-router-dom';
import AuthUser from "../../components/AuthUser";

const leftbox = {
  background: '#007f66'
};

export default function Login() {
  const { http, setToken } = AuthUser();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = () => {
    // Clear any previous error message
    setErrorMessage('');

    // Perform basic form validation
    if (!email.trim() || !password.trim()) {
      setErrorMessage('Email and password are required');
      return;
    }

    // Make API call to authenticate user
    http.post('/login', { email, password })
      .then((res) => {
        // Set user token and navigate to dashboard on successful login
        setToken(res.data.user, res.data.access_token);
        navigate('/dashboard');
      })
      .catch(error => {
        // Set error message if login fails
        setErrorMessage(error.response?.data?.message || 'Login failed');
      });
  };

  return (
    <div className='container d-flex justify-content-center align-items-center min-vh-100'>

      <div className='row border rounded-4 p-3 bg-white shadow box-area'>
        <div className="col-md-6 rounded-4 d-flex justify-content-center align-items-center flex-column left-box" style={leftbox}>
          <div className='featured-image mb-3'>
            <img src={Logo} alt="logo" className='img-fluid' style={{ width: '500px' }} />
          </div>
        </div>
        <div className="col-md-6 rounded-4 rigth-box">
          <form onSubmit={handleSubmit}>
            <div className="row align-items-center">
              <div className="header-text mb-4">
                <p className='text-center fs-2'>Business Management System </p>
                {errorMessage && <p className="text-danger">{errorMessage}</p>}
              </div>
              <div className="input-group mb-3">
                <input type="text" className='form-control form-control-lg bg-light fs-6' placeholder='Email Address' value={email} onChange={(e) => setEmail(e.target.value)} />
              </div>
              <div className="input-group mb-1">
                <input type="password" className='form-control form-control-lg bg-light fs-6' placeholder='Password' value={password} onChange={(e) => setPassword(e.target.value)} />
              </div>
              <div className="input-group mb-5 d-flex justify-content-between">
                <div className="form-check">
                  <input type="checkbox" className='form-check-input' id="formCheck" />
                  <label htmlFor="formCheck" className='form-check-lable text-secondary'><small>Remember Me</small></label>
                </div>
                <div className='forgot'>
                  <small><a href="#">Forgot Password?</a></small>
                </div>
              </div>
              <div className="input-group mb-3">
                <button className='btn btn-lg btn-primary w-100 fs-6' type="submit">Login</button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  )

}
