import React, { useEffect, useState } from "react";
import axios from "axios";
import "./allemp.css";
import Header from "../../../components/Header";
import Sidebar from "../../../components/Sidebar";
import PageTitle from "../../../components/PageTitle/PageTitle";
function AllEmp() {
  const pages = ["All Employees"];
  const icon = "bi bi-house-up";

  function Employee(){
    const [id,setId] = useState('');
    const[fname,setFname] = useState('');
    const [lname,setLname] = useState('');
    const [email,setEmail] = useState('');
    const [nic,setNic] = useState('');
    const [phone,setPhone] = useState('');
    const [address,setAddress] = useState('');
    const [gender,setGender] = useState('');
    const [emptype,setEmptype] = useState('');
    const [employees,setEmployees] = useState('');

    useEffect(()=>{
      (async () => await Load())();
  }, []);
  async function  Load()
  {
     const result = await axios.get(
         "http://127.0.0.1:8000/api/employee");
         setEmployees(result.data);
         console.log(result.data);
  }
 
    
  async function save(event)
    {
        event.preventDefault();
    try
        {
         await axios.post("http://127.0.0.1:8000/api/employee",
        {
        
         
        
        });
          alert("Employee registration Registation Successfully");
          setId("");
          setFname("");
          setLname("");
          setEmail("");
          setNic("");
          setPhone("");
          setAddress("");
          setGender("");
          setEmptype("");
          Load();
        
        }
    catch(err)
        {
          alert("employee Registation Failed");
        }
   }

   async function editEmployees(employees)
   {
      setFname(employees.fname);
      setLname(employees.lname);
      setEmail(employees.email);
      setNic(employees.nic);
      setPhone(employees.phone);
      setAddress(employees.address);
      setGender(employees.gender);
      setEmptype(employees.emptype);
      setId(employees.id);
       
   }

   async function DeleteStudent(id)
   {
       
        await axios.delete("http://127.0.0.1:8000/api/employee/" + id); 
        alert("Employee deleted Successfully");
        Load();
   
   }

   async function update(event)
   {
    event.preventDefault();
   try
       {
        
        await axios.put("http://127.0.0.1:8000/api/employee/"+ employees.find(u => u.id === id).id || id,
       {
         id: id,
         fname:fname,
         lname:lname,
         email:email,
         nic:nic,
         phone:phone,
         address:address,
         gender:gender,
         emptype:emptype
       
       });
         alert("Employee registraton updated");
         setId("");
         setFname("");
         setLname("");
         setEmail("");
         setNic("");
         setPhone("");
         setAddress("");
         setGender("");
         setEmptype("");
         
         Load();
       
       }
   catch(err)
       {
         alert("User Registation Failed");
       }



   
      }
  }
  return (
    <>
      <Header />
      <Sidebar />
      <PageTitle page="All Employees" pages={pages} icon={icon} />
      <main id="main" className="main">
      <div className="container">
    <div className="row">
        <div className="">
            <div class="signup-form">
                <form action="" className="mt-5 border p-4 bg-light shadow">
                    <h4 className="mb-5 text-secondary">Employee Details</h4>
                    <div className="row">
                        <div className="mb-3 col-md-6">
                            <label>First Name<span class="text-danger">*</span></label>
                            <input type="text" name="fname" className="form-control" placeholder="Enter First Name"
                          
                            />
                        </div>

                        <div class="mb-3 col-md-6">
                            <label>Last Name<span class="text-danger">*</span></label>
                            <input type="text" name="Lname" class="form-control" placeholder="Enter Last Name"
                            />
                        </div>
                        <div class="mb-3 col-md-6">
                            <label>E mail<span class="text-danger">*</span></label>
                            <input type="text" name="email" class="form-control" placeholder="Enter Email"/>
                        </div>

                        <div class="mb-3 col-md-6">
                            <label>NIC<span class="text-danger">*</span></label>
                            <input type="text" name="nic" class="form-control" placeholder="Enter NIC"/>
                        </div>

                        <div class="mb-3 col-md-6">
                            <label>Contact Number<span class="text-danger">*</span></label>
                            <input type="text" name="phone" class="form-control" placeholder="Enter contact number"/>
                        </div>

                        <div class="mb-3 col-md-6">
                            <label>Address<span class="text-danger">*</span></label>
                            <input type="text" name="address" class="form-control" placeholder="Enter Address"/>
                        </div>
                        <div class="mb-3 col-md-6">
                        <label>Genger<span class="text-danger">*</span></label>
                        <select name="gender" class="form-control">
                            <option value="">Select Gender</option>
                            <option value="male">Male</option>
                            <option value="female">Female</option>
                            
                        </select>
                        </div>
                        <div class="mb-3 col-md-6">
                        <label>Employee type<span class="text-danger">*</span></label>
                        <select name="employeetype" class="form-control">
                            <option value="">Select Employee type</option>
                            <option value="male">Manager</option>
                            <option value="female">Worker</option>
                            
                        </select>
                        </div>

                       

                      
                        <div class="col-md-12">
                           <button class="btn btn-primary float-end">Signup Now</button>
                           <button class="btn btn-primary float-end">Signup Now</button>
                        </div>
                    </div>
                </form>
               
            </div>
        </div>
    </div>
    </div>
      </main>
    </>
  );
}

export default AllEmp;
