import React, { useState } from "react";
import "./login.css"; // Assuming you have a separate SCSS file
import Logo from './Logo.png'
import axios from '../../Api/axios.js';
import "bootstrap/dist/css/bootstrap.min.css";
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import { useNavigate } from 'react-router-dom';


const leftbox = {
  background: '#007f66'
};

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const csrfToken = async () => {
		await axios.get('http://localhost:8000/sanctum/csrf-cookie');
		return true;
	};

  const handleSubmit = async (event) => {
    event.preventDefault(); // Prevent default form submission

    setIsLoading(true); // Set loading state
    setErrorMessage(''); // Clear any previous errors

    try {

      csrfToken();
      
      if (validate()) {
        
        const response =  await axios.post('/login', {
          email,
          password,
        });

        // Handle successful login (e.g., store token, redirect to dashboard)
        console.log('Login successful:', response.data); // Example logging
        navigate('/Dashborad');
      }
    } catch (error) {
      setErrorMessage(error.response?.data?.message || 'Login failed');
      console.error("Login failed:", error); // Handle errors gracefully
    } finally {
      setIsLoading(false); // Reset loading state
    }
  };

  const validate = () => {
    let errors = {};
    let isValid = true;

    if (!email.trim()) {
      errors.email = "Email is required";
      isValid = false;
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      errors.email = "Invalid Email Address";
      isValid = false;
    }

    if (!password.trim()) {
      errors.password = "Password is required";
      isValid = false;
    }else if(password.length < 8){
      errors.password = "Password must be at least 8 characters.";
      isValid=false;
      // alert("Password not match..!")
    }
    
    // else if(password.length< 12){
    //   errors.password = " Wrong Password"
    //   isValid=false;
    // }

    setErrors(errors);
    return isValid;
  };

  return (
    <div className='container d-flex justify-content-center align-items-center min-vh-100'>
     
      <div className='row border rounded-4 p-3 bg-white shadow box-area'>
        <div className="col-md-6 rounded-4 d-flex justify-content-center align-items-center flex-column left-box" style={leftbox}>
          <div className='featured-image mb-3'>
            <img src={Logo} alt="logo" className='img-fluid' style={{ width: '500px' }} />
          </div>
        </div>
        <div className="col-md-6 rounded-4 rigth-box">
          <form onSubmit={handleSubmit}>
            <div className="row align-items-center">
              <div className="header-text mb-4">
                <p className='text-center fs-2'>Business Management System </p>
                
                
                {/* {errors.email && <label className="text-danger" htmlFor="email">{errors.email}</label>} <br />
                {errors.password && <label className="text-danger" htmlFor="pwd">{errors.password}</label>} */}
              </div>
              <div className="input-group mb-3">
                <input type="text" className='form-control form-control-lg bg-light fs-6' placeholder='Email Address' id="email" value={email} onChange={(e) => setEmail(e.target.value)} /> 
               
              </div>
              {errors.email &&<div class="alert alert alert-danger">
                {errors.email}
                </div>}
              <div className="input-group mb-1">
                <input type="password" className='form-control form-control-lg bg-light fs-6' id="pwd" placeholder='Password' value={password} onChange={(e) => setPassword(e.target.value)} /> 
               
              </div>
              {errors.password &&<div class="alert alert-danger">
                {errors.password}
                </div>}
              <div className="input-group mb-5 d-flex justify-content-between">
                <div className="form-check">
                  <input type="checkbox" className='form-check-input' name="" id="formCheck" />
                  <label htmlFor="formCheck" className='form-check-lable text-secondary'><small>Remember Me</small></label>
                </div>
                <div className='forgot'>
                  <small><a href="#">Forgot Password?</a></small>
                </div>
              </div>
              <div className="input-group mb-3">
                <button className='btn btn-lg btn-primary w-100 fs-6' type="submit" disabled={isLoading}> {isLoading ? 'Loading...' : 'Login'}</button>
                {errorMessage && <p className="error-message">{errorMessage}</p>}
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
