import React, { useEffect, useState } from "react";
import "./dailytasks.css";
import Header from "../../../components/Header";
import Sidebar from "../../../components/Sidebar";
import PageTitle from "../../../components/PageTitle/PageTitle";
import axios from "axios";
function DailyTasks() {
  const pages = ["DailyTasks "];
  const icon = "bi bi-house-up";

  const [empid,setEmpId] = useState('');
  const [task,setTask] = useState('');
  const [date,setDate] = useState('');
  const [status,setStatus] = useState('');
  const [tasks,setTasks] = useState('');

   useEffect(() => {
    (async () => await Load())();
    }, []);

    async function Load(){
      const result = await axios.get(
        "http://127.0.0.1:8000/api/task"
      );
      setTasks(result.data);
      console.log(result.data);
    }

    async function save(event)
    {
        event.preventDefault();
    try
        {
         await axios.post("http://127.0.0.1:8000/api/task",
        {
          empid:empid,
          task:task,
          date:date,
          status:status,
          
        
        });
          alert("Task Saved");
          setEmpId("");
          setTask("");
          setDate("");
          setStatus("");
          Load();
        
        }
    catch(err)
        {
          alert("Task Save Failed");
        }
   }

   

  return (
    <>
      <Header />
      <Sidebar />
      <PageTitle page="Daily Tasks" pages={pages} icon={icon} />
      <main id="main" className="main"  style={{ marginTop: "10px" }}>
      <div class="container">
    <div class="row">
        <div class="">
            <div class="signup-form">
                <form action="" class="mt-5 border p-4 bg-light shadow">
                    <h4 class="mb-5 text-secondary">Daily Task</h4>
                    <div class="row">
                        <div class="mb-3 col-md-6">
                            <label>Employee ID<span class="text-danger">*</span></label>
                            <input type="text"  className="form-control" placeholder="Employee ID"/>
                        </div>
                        <div class="mb-3 col-md-6">
                            <label>Task<span className="text-danger">*</span></label>
                            <input type="text" name="fname" class="form-control" placeholder="Enter Task"/>
                        </div>
                      
                        <div class="mb-3 col-md-6">
                            <label>Date<span class="text-danger">*</span></label>
                            <input type="date" className="form-control" placeholder="Select Date"/>
                        </div>

                        <div class="mb-3 col-md-6">
                        <label>Status<span className="text-danger">*</span></label>
                        <select
                            name="Status"
                            className="form-control"
                            
                        >
                            <option value="">Select Status</option>
                            <option value="Manager">Penging</option>
                            <option value="Worker">Done</option>
                            <option value="Accountan">Start</option>
                        </select>
                        
                        </div>

                        <div class="col-md-12">
                           <button class="btn btn-primary float-end" style={{marginRight:"10px"}}>Save</button>
                           <button class="btn btn-primary float-end" style={{marginRight:"10px"}}>Update</button>
                           <button class="btn btn-primary float-end" style={{marginRight:"10px"}}>Reset</button>
                        </div>
                    </div>
                </form>
               
            </div>
        </div>
    </div>
</div>
      </main>
    </>
  );
}

export default DailyTasks;
